    n = int(input())
    edges = []
    for _ in range(n - 1):
        u, v = map(int, input().split())
        edges.append((u, v))

    def find_root(edges, n):
        adj = [[] for _ in range(n + 1)]
        for u, v in edges:
            adj[u].append(v)
            adj[v].append(u)

        def dfs(node, parent, visited):
            visited[node] = True
            for neighbor in adj[node]:
                if not visited[neighbor]:
                    if dfs(neighbor, node, visited):
                        return True
                elif neighbor != parent:
                    return True
            return False

        visited = [False] * (n + 1)
        for i in range(1, n + 1):
            if not visited[i]:
                if dfs(i, -1, visited):
                    return i
        return -1

    root = find_root(edges, n)

    def find_path(edges, root, n):
        adj = [[] for _ in range(n + 1)]
        for u, v in edges:
            adj[u].append(v)
            adj[v].append(u)

        path = []
        visited = [False] * (n + 1)
        def dfs(node, parent):
            visited[node] = True
            path.append(node)
            for neighbor in adj[node]:
                if not visited[neighbor]:
                    dfs(neighbor, node)
            path.append(node)

        dfs(root, -1)
        return path

    path = find_path(edges, root, n)

    def find_coordinates(path, n):
        coordinates = {}
        for i in range(n):
            coordinates[path[i]] = [0, 0]
        for i in range(n - 1):
            u = path[i]
            v = path[i + 1]
            if u < v:
                coordinates[v][0] = coordinates[u][0] + 1
            else:
                coordinates[v][0] = coordinates[u][0] - 1
            if u < v:
                coordinates[v][1] = coordinates[u][1]
            else:
                coordinates[v][1] = coordinates[u][1] + 1
        return coordinates

    coordinates = find_coordinates(path, n)

    def check_solution(edges, coordinates):
        for u, v in edges:
            if coordinates[u][0] == coordinates[v][0] and coordinates[u][1] == coordinates[v][1]:
                return False
            if coordinates[u][0] == coordinates[v][0] and coordinates[u][1] != coordinates[v][1]:
                return False
            if coordinates[u][1] == coordinates[v][1] and coordinates[u][0] != coordinates[v][0]:
                return False
        return True

    if check_solution(edges, coordinates):
        print('YES')
