def solve():
    n = int(input())
    s = input()

    def is_correct(s):
        return all(c in '01' for c in s) and s[0] != '0'

    def swap(s, i, j):
        s = list(s)
        s[i], s[j] = s[j], s[i]
        return ''.join(s)

    def replace(s):
        new_s = ''
        i = 0
        while i < len(s):
            if i + 1 < len(s) and s[i:i+2] == '11':
                new_s += '1'
                i += 2
            else:
                new_s += s[i]
                i += 1
        return new_s

    def find_min(s):
        if not is_correct(s):
            return s

        min_s = s

        # Swap operation
        for i in range(len(s) - 1):
            new_s = swap(s, i, i+1)
            if is_correct(new_s):
                min_s = min(min_s, find_min(new_s))

        # Replace operation
        new_s = replace(s)
        if is_correct(new_s):
            min_s = min(min_s, find_min(new_s))

        return min_s

    print(find_min(s))


