def solve():
    w, n = map(int, input().split())
    lines = []
    for _ in range(n):
        lines.append(list(map(int, input().split())))

    def calculate_area(line1, line2):
        x1, y1, x2, y2 = line1
        x3, y3, x4, y4 = line2

        # Calculate the intersection point of the two lines
        if (x2 - x1) == 0:
            x = x1
            y = y3 + (y4 - y3) * (x - x3) / (x4 - x3)
        elif (x4 - x3) == 0:
            x = x3
            y = y1 + (y2 - y1) * (x - x1) / (x2 - x1)
        else:
            x = (y1 - y3 + (x3 - x1) * (y4 - y3) / (x4 - x3)) / \
                ((y2 - y1) - (y4 - y3) * (x3 - x1) / (x2 - x1))
            y = y1 + (y2 - y1) * (x - x1) / (x2 - x1)

        # Check if the intersection point is on both lines
        if (min(x1, x2) <= x <= max(x1, x2) and
                min(y1, y2) <= y <= max(y1, y2) and
                min(x3, x4) <= x <= max(x3, x4) and
                min(y3, y4) <= y <= max(y3, y4)):
            return True
        else:
            return False

    def check_division(lines):
        num_sectors = 1
        for i in range(len(lines)):
            for j in range(i + 1, len(lines)):
                if calculate_area(lines[i], lines[j]):
                    num_sectors += 1
        return num_sectors

    def add_lines(lines, w):
        num_sectors = check_division(lines)
        if num_sectors == w:
            return 0

        best_lines = lines[:]
        min_diff = float('inf')

        for i in range(len(lines)):
            for j in range(i + 1, len(lines)):
                new_lines = lines[:]
                new_lines.append([lines[i][0], lines[i][1], lines[j][2], lines[j][3]])
                num_sectors = check_division(new_lines)
                diff = abs(num_sectors - w)
                if diff < min_diff:
                    min_diff = diff
                    best_lines = new_lines

        return len(best_lines) - len(lines)

    print(add_lines(lines, w))


