        max_dist = 0
        visited = set()
        visited.add(start_pos)

        def dfs(pos, dist):
            nonlocal max_dist
            max_dist = max(max_dist, dist)

            for dx, dy in [(0, 1), (0, -1), (1, 0), (-1, 0)]:
                nx, ny = pos[0] + dx, pos[1] + dy
                if 0 <= nx < w and 0 <= ny < h:
