                    if new_pos not in visited and is_safe(new_pos, time):
                        visited.add(new_pos)
                        dfs(new_pos, dist + 1)
                        visited.remove(new_pos)

        dfs(start_pos, 0)
        return max_dist

    max_dist = 0
