def solve():
    n, m = map(int, input().split())
    cities = [input() for _ in range(n)]
    edges = []
    for _ in range(m):
        u, v, c = input().split()
        u_idx = cities.index(u)
        v_idx = cities.index(v)
        edges.append((u_idx, v_idx, int(c)))

    assignments = []
    for _ in range(4):
        u, v = input().split()
        u_idx = cities.index(u)
        v_idx = cities.index(v)
        assignments.append((u_idx, v_idx))

    def find(parent, i):
        if parent[i] == i:
            return i
        return find(parent, parent[i])

    def union(parent, rank, x, y):
        xroot = find(parent, x)
        yroot = find(parent, y)

        if rank[xroot] < rank[yroot]:
            parent[xroot] = yroot
        elif rank[xroot] > rank[yroot]:
            parent[yroot] = xroot
        else:
            parent[yroot] = xroot
            rank[xroot] += 1

    def is_connected(edges, u, v):
        parent = list(range(n))
        rank = [0] * n

        for u, v, _ in edges:
            u_root = find(parent, u)
            v_root = find(parent, v)
            if u_root == v_root:
                return True
            union(parent, rank, u_root, v_root)

        return False

    def calculate_cost(edges, assignment):
        u, v = assignment
        cost = 0
        for u_idx, v_idx, c in edges:
            if (u_idx == u and v_idx == v) or (u_idx == v and v_idx == u):
                cost += c
        return cost

    min_cost = float('inf')
    for i in range(1 << m):
        current_edges = []
        for j in range(m):
            if (i >> j) & 1:
                current_edges.append(edges[j])

        valid = True
        for u, v in assignments:
            if not is_connected(current_edges, u, v):
                valid = False
                break

        if valid:
            cost = 0
            for u, v in assignments:
                cost += calculate_cost(current_edges, (u, v))
            min_cost = min(min_cost, cost)

    print(min_cost)


solve()

