        for i in range(1, int(n**0.5) + 1):
            if n % i == 0:
                divisors.append(i)
                if i * i != n:
                    divisors.append(n // i)
        return sorted(divisors)

    def find_valid_piles(arr):
        divisors = find_divisors(find_lcm(arr))
        valid_piles = []
        for i in range(1, len(divisors) + 1):
            valid_piles.append([])
        for num in arr:
            for i in range(len(divisors)):
                if num % divisors[i] == 0:
                    valid_piles[i].append(num)
                    break
        return valid_piles

    def count_distinct_piles(piles):
