def solve_game(start_pos, end_pos, positions):
    if start_pos == end_pos:
        return 0

    visited = {}
    queue = [(start_pos, 0)]
    visited[start_pos] = True

    while queue:
        curr_pos, curr_round = queue.pop(0)

        if curr_pos == end_pos:
            return curr_round

        options = positions[curr_pos]

        for option in options:
            for pos in option:
                if pos not in visited:
                    visited[pos] = True
                    queue.append((pos, curr_round + 1))

    return -1


