    b = list(map(int, input().split()))
    edges = []
    for _ in range(m):
        u, v = map(int, input().split())
        edges.append((u - 1, v - 1))

    def calculate_profit(removed_vertices):
        remaining_vertices = [i for i in range(n) if i not in removed_vertices]

        graph = [[] for _ in range(n)]
        for u, v in edges:
            if u in remaining_vertices and v in remaining_vertices:
                graph[u].append(v)
                graph[v].append(u)

        components = []
        visited = [False] * n
        for i in remaining_vertices:
            if not visited[i]:
                component = []
                stack = [i]
                visited[i] = True
                while stack:
                    node = stack.pop()
                    component.append(node)
                    for neighbor in graph[node]:
                        if not visited[neighbor]:
                            visited[neighbor] = True
                            stack.append(neighbor)
                components.append(component)

        score = 0
        for component in components:
            component_sum_b = sum(b[i] for i in component)
            score += abs(component_sum_b)

        total_cost = sum(a[i] for i in removed_vertices)

        return score - total_cost

    max_profit = 0
    for i in range(1 << n):
        removed_vertices = []
        for j in range(n):
            if (i >> j) & 1:
                removed_vertices.append(j)

        profit = calculate_profit(removed_vertices)
        max_profit = max(max_profit, profit)

    print(max_profit)


