            neighbors = []
            r, c = pos

            # Check right
            if c + 1 <= m and (r, c + 1) not in visited:
                neighbors.append((r, c + 1))

            # Check left
            if c - 1 >= 1 and (r, c - 1) not in visited:
                neighbors.append((r, c - 1))

            # Check up
            if r - 1 >= 1 and (r - 1, c) not in visited:
                neighbors.append((r - 1, c))

            # Check down
            if r + 1 <= n and (r + 1, c) not in visited:
                neighbors.append((r + 1, c))

            return neighbors

        def bfs(initial, target):
            q = collections.deque([(initial, [])])
            visited = {tuple(initial)}

            while q:
                current, path = q.popleft()

                if current == target:
                    return path

                r, c = current

                # Try moving right
                if c + 1 <= m:
                    new_pos = (r, c + 1)
                    if new_pos not in visited:
                        new_path = path + [('>', c + 1)]
                        q.append((new_pos, new_path))
                        visited.add(tuple(new_pos))

                # Try moving left
                if c - 1 >= 1:
                    new_pos = (r, c - 1)
                    if new_pos not in visited:
                        new_path = path + [('<', c - 1)]
                        q.append((new_pos, new_path))
                        visited.add(tuple(new_pos))

                # Try moving up
                if r - 1 >= 1:
                    new_pos = (r - 1, c)
                    if new_pos not in visited:
                        new_path = path + [('^', r - 1)]
                        q.append((new_pos, new_path))
                        visited.add(tuple(new_pos))

                # Try moving down
                if r + 1 <= n:
                    new_pos = (r + 1, c)
                    if new_pos not in visited:
                        new_path = path + [('v', r + 1)]
                        q.append((new_pos, new_path))
                        visited.add(tuple(new_pos))

            return None

        path = bfs(initial, target)
        if path:
            print('possible')
