def solve():
    n = int(input())
    edges = []
    for _ in range(2 * (n - 1)):
        u, v = map(int, input().split())
        edges.append((u, v))

    def check_assignment(assignment):
        left_edges = []
        right_edges = []
        for i in range(2 * (n - 1)):
            if assignment[i] == 'L':
                left_edges.append(edges[i])
            else:
                right_edges.append(edges[i])

        left_tree = build_tree(left_edges)
        right_tree = build_tree(right_edges)

        if left_tree is None or right_tree is None:
            return False

        if len(left_tree) != n or len(right_tree) != n:
            return False

        if not is_left_to_right(left_tree):
            return False

        if not is_right_to_left(right_tree):
            return False

        return True

    def build_tree(edges):
        adj = [[] for _ in range(n + 1)]
        for u, v in edges:
            adj[u].append(v)
            adj[v].append(u)

        visited = [False] * (n + 1)
        root = None

        for i in range(1, n + 1):
            if not visited[i]:
                root = i
                break

        if root is None:
            return None

        tree = []
        stack = [(root, -1)]

        while stack:
            node, parent = stack.pop()
            tree.append((node, parent))

            visited[node] = True

            for neighbor in adj[node]:
                if not visited[neighbor]:
                    stack.append((neighbor, node))

        return tree

    def is_left_to_right(tree):
        for node, parent in tree:
            if node > parent:
                return False
        return True

    def is_right_to_left(tree):
        for node, parent in tree:
            if node < parent:
                return False
        return True

    def find_all_assignments():
        assignments = []
        for i in range(1 << (2 * (n - 1))):
            assignment = []
            for j in range(2 * (n - 1)):
                if (i >> j) & 1:
                    assignment.append('L')
                else:
                    assignment.append('R')
            assignments.append(''.join(assignment))
        return assignments

    assignments = find_all_assignments()

    for assignment in assignments:
        if check_assignment(assignment):
            print(assignment)
            return

    print('impossible')


solve()

