    s = input()
    n = len(s)

    def calculate_weight(factored_string):
        weight = 0
        i = 0
        while i < len(factored_string):
            if factored_string[i] == '(':
                j = i
                while factored_string[j] != ')':
                    j += 1
                exponent_start = j + 1
                exponent_end = j + 1
                while exponent_end < len(factored_string) and \
                        factored_string[exponent_end].isdigit():
                    exponent_end += 1
                exponent = int(factored_string[exponent_start:exponent_end])
                weight += (exponent - 1) * \
                    (j - i - 1) + calculate_weight(factored_string[j+1:exponent_end])
                i = exponent_end
            else:
                weight += 1
                i += 1
        return weight

    def find_maximal_factoring(s):
        n = len(s)
        for i in range(1, n):
            for j in range(i + 1, n + 1):
                sub = s[:j]
                if len(sub) > 0:
                    repetitions = n // len(sub)
                    factored_string = '(' + sub + '^' + str(repetitions) + ')'
                    if calculate_weight(factored_string) < calculate_weight(s):
                        return find_maximal_factoring(factored_string)
        return s

    maximal_factoring = find_maximal_factoring(s)
    print(calculate_weight(maximal_factoring))


