    n = int(input())
    edges = []
    for _ in range(n - 1):
        u, v = map(int, input().split())
        edges.append((u, v))

    adj = [[] for _ in range(n + 1)]
    for u, v in edges:
        adj[u].append(v)
        adj[v].append(u)

    def bfs(start):
        dist = [-1] * (n + 1)
        dist[start] = 0
        q = [start]
        while q:
            u = q.pop(0)
            for v in adj[u]:
                if dist[v] == -1:
                    dist[v] = dist[u] + 1
                    q.append(v)
        return dist

    dist1 = bfs(1)

    ans = 0
    for i in range(1, n + 1):
        if dist1[i] == -1:
            continue
        if dist1[i] > 2:
            ans += 1

    print(ans)


