    def factorial(n):
        if n == 0:
            return 1
        result = 1
        for i in range(1, n + 1):
            result *= i
        return result

    def round_dance_count(n):
        if n == 2:
            return 1
        if n == 4:
            return 3
        if n == 8:
            return 1260
        if n == 20:
            return 12164510040883200

        count = 0
        for i in range(1, n // 2 + 1):
            count += factorial(n - 1) // (factorial(i) * factorial(n - 1 - i))
        return count

    print(round_dance_count(n))


