def solve():
    n, m = map(int, input().split())
    grid = []
    for _ in range(n):
        grid.append(input())

    def is_valid_placement(grid, x, y):
        for i in range(n):
            for j in range(m):
                if grid[i][j] == 'E':
                    if (i > 0 and grid[i-1][j] == 'E') or \
                       (i < n-1 and grid[i+1][j] == 'E') or \
                       (j > 0 and grid[i][j-1] == 'E') or \
                       (j < m-1 and grid[i][j+1] == 'E'):
                        return False
        return True

    def place_coffee_shops(grid, x, y):
        new_grid = [list(row) for row in grid]
        new_grid[x][y] = 'E'
        return new_grid

    def backtrack(grid, x, y):
        if x == n:
            if is_valid_placement(grid, x, y):
                for row in grid:
                    print(''.join(row))
            return

        if y == m:
            backtrack(grid, x + 1, 0)
            return

        if grid[x][y] == '.':
            new_grid = place_coffee_shops(grid, x, y)
            backtrack(new_grid, x, y + 1)
        else:
            backtrack(grid, x, y + 1)

    backtrack(grid, 0, 0)


