def solve():
    n, x = map(int, input().split())
    a = list(map(int, input().split()))

    ans = float('inf')

    for i in range(1 << n):
        temp_a = a[:]
        operations = 0

        for j in range(n):
            if (i >> j) & 1:
                if temp_a[j] > 0:
                    temp_a[j] -= 1
                    operations += 1

        valid = True
        for j in range(n - 1):
            if temp_a[j] + temp_a[j+1] > x:
                valid = False
                break

        if valid:
            ans = min(ans, operations)

    print(ans)


