def solve():
    n, m = map(int, input().split())
    edges = []
    for _ in range(m):
        u, v = map(int, input().split())
        edges.append((u, v))

    def check_orientation(orientation):
        adj = [[] for _ in range(n)]
        for i in range(m):
            if orientation[i] == '0':
                adj[edges[i][0] - 1].append(edges[i][1] - 1)
            else:
                adj[edges[i][1] - 1].append(edges[i][0] - 1)

        def dfs(node, visited, parent):
            visited[node] = True
            for neighbor in adj[node]:
                if not visited[neighbor]:
                    if dfs(neighbor, visited, node):
                        return True
                elif neighbor != parent:
                    return True
            return False

        visited = [False] * n
        for i in range(n):
            if not visited[i]:
                if dfs(i, visited, -1):
                    return False
        return True

    def find_orientation():
        import itertools
        for orientation in itertools.product('01', repeat=m):
            if check_orientation(''.join(orientation)):
                return ''.join(orientation)
        return None

    orientation = find_orientation()
    if orientation is None:
        print('NO')
    else:
        print('YES')
        print(orientation)


