def solve():
    n, a, b = map(int, input().split())
    ghosts = []
    for _ in range(n):
        x, vx, vy = map(int, input().split())
        ghosts.append((x, vx, vy))

    def find_collision_time(ghost1, ghost2):
        x1, vx1, vy1 = ghost1
        x2, vx2, vy2 = ghost2
        if vx1 == vx2 and vy1 == vy2:
            return None  # No collision if velocities are the same
        if vx1 == vx2:
            t = (x2 - x1) / (vy2 - vy1)
            if t >= 0:
                return t
            return None
        if vy1 == vy2:
            t = (x2 - x1) / (vx2 - vx1)
            if t >= 0:
                return t
            return None
        # General case
        denominator = (vx1 - vx2) * (vy2 - vy1) - (vy1 - vy2) * (vx2 - vx1)
        if denominator == 0:
            return None  # Parallel lines
        t1 = ((x2 - x1) * (vy2 - vy1) - (vy1 - vy2) * (x2 - x1)) / denominator
        t2 = ((x1 - x2) * (vy1 - vy2) - (vy1 - vy2) * (x1 - x2)) / denominator
        if t1 >= 0 and t2 >= 0:
            return min(t1, t2)
        return None

    collisions = []
    for i in range(n):
        for j in range(i + 1, n):
            collision_time = find_collision_time(ghosts[i], ghosts[j])
            if collision_time is not None:
                collisions.append((i, j, collision_time))

    collisions.sort(key=lambda x: x[2])

    experience_index = 0
    for i in range(n):
        for j in range(i + 1, n):
            if (i, j) in collisions or (j, i) in collisions:
                experience_index += 2

    print(experience_index)

