import math

def distance(x1, y1, x2, y2):
    return math.sqrt((x1 - x2) ** 2 + (y1 - y2) ** 2)

def area(x, y, r):
    return math.pi * r ** 2

def velocity(x1, y1, v1x, v1y, x2, y2, v2x, v2y):
    dx = x2 - x1
    dy = y2 - y1
    dvx = v2x - v1x
    dvy = v2y - v1y
    if dx == 0 and dy == 0:
        return (0, 0)
    else:
        return (dx * dvx + dy * dvy) / (dx ** 2 + dy ** 2), (dx * dvy - dy * dvx) / (dx ** 2 + dy ** 2)

def agglomerate(x1, y1, r1, v1x, v1y, x2, y2, r2, v2x, v2y):
    dx = x2 - x1
    dy = y2 - y1
    dvx = v2x - v1x
    dvy = v2y - v1y
    if dx == 0 and dy == 0:
        return (0, 0)
    else:
        return (dx * dvx + dy * dvy) / (dx ** 2 + dy ** 2), (dx * dvy - dy * dvx) / (dx ** 2 + dy ** 2)

