def solve():
    n = int(input())

    mod = 10**9 + 7

    # dp[i][j] represents the number of valid strings of length i that end with j
    # where j is a character (A, C, G, T)
    dp = [[0 for _ in range(4)] for _ in range(n+1)]

    # Base case: strings of length 1
    for i in range(4):
        dp[1][i] = 1

    # Fill the dp table
    for i in range(2, n+1):
        for j in range(4):
            for k in range(4):
                if j != 2 or k != 1:
                    dp[i][j] = (dp[i][j] + dp[i-1][k]) % mod

    # Sum up the number of valid strings of length n
    result = 0
    for i in range(4):
        result = (result + dp[n][i]) % mod

    print(result)

