    for _ in range(n - 1):
        a, b = map(int, input().split())
        graph[a - 1].append(b - 1)
        graph[b - 1].append(a - 1)

    def bfs(start):
        visited = [False] * n
        visited[start] = True
        queue = [start]
        distance = [0] * n
        distance[start] = 0

        while queue:
            current = queue.pop(0)
            for neighbor in graph[current]:
                if not visited[neighbor]:
                    visited[neighbor] = True
                    queue.append(neighbor)
                    distance[neighbor] = distance[current] + 1
        return distance

    distances = []
    for i in range(n):
        distances.append(bfs(i))

    count = 0
    for i in range(n):
        for j in range(m):
            if distances[i][p[j] - 1] <= d:
                count += 1
                break

    print(count)

