def solve():
    n = int(input())
    costs = list(map(int, input().split()))
    m = int(input())
    roads = []
    for _ in range(m):
        u, v = map(int, input().split())
        roads.append((u - 1, v - 1))

    MOD = 1000000007

    # Build a graph
    graph = [[] for _ in range(n)]
    for u, v in roads:
        graph[u].append(v)

    # DP to find the minimum cost to protect all junctions
    dp = [[float('inf')] * (1 << n) for _ in range(n)]
    for i in range(n):
        dp[i][1 << i] = costs[i]

    for mask in range(1 << n):
        for i in range(n):
            if mask & (1 << i):
                for j in graph[i]:
                    if mask & (1 << j):
                        dp[i][mask] = min(dp[i][mask], dp[j][mask ^ (1 << i)] + costs[i])

    min_cost = float('inf')
    min_masks = []
    for i in range(n):
        min_cost = min(min_cost, dp[i][(1 << n) - 1])
        if min_cost == dp[i][(1 << n) - 1]:
            min_masks.append((i, (1 << n) - 1))

    # Count the number of ways to achieve the minimum cost
    ways = 0
    for i, mask in min_masks:
        ways = (ways + 1) % MOD

    print(min_cost, ways)

