            if is_valid_set_list(set_list):
                if len(set_list) < min_length:
                    min_length = len(set_list)
                    min_set_list = sorted(set_list)
        return min_length, min_set_list

    min_length, min_set_list = find_min_set_list()
    print(min_length)
