    def expected_score(cards, c):
        if not cards:
            return 0
        if c == 0:
            return cards[0]

        max_seen = -1
        for i in range(c):
            if cards[i] > max_seen:
                max_seen = cards[i]

        expected_score_after_c = 0
        for i in range(c):
            if cards[i] > max_seen:
                expected_score_after_c += expected_score(cards[i+1:], c-1)
            else:
                expected_score_after_c += expected_score(cards[i+1:], c)

        return expected_score_after_c / (c + 1)

    print(expected_score(cards, c))

