        visited = [False] * (n + 1)
        queue = [(1, 0)]

        while queue:
            room, steps = queue.pop(0)

            if room == n:
                return True

            if visited[room]:
                continue

            visited[room] = True

            for i in range(1, 5):
                next_room = config[room - 1][i - 1]
                if next_room != 0:
                    queue.append((next_room, steps + 1))

        return False

    if not can_reach_dormitory(senior_student_config):
        print("Impossible")
        return

    if can_reach_dormitory(your_config):
        print("Yes")
    else:
