def solve():
    n, k = map(int, input().split())
    w = list(map(int, input().split()))

    MOD = 10**9 + 7

    def factorial(n):
        if n == 0:
            return 1
        result = 1
        for i in range(1, n + 1):
            result = (result * i) % MOD
        return result

    def inverse(n):
        return pow(n, MOD - 2, MOD)

    def combination(n, r):
        return (factorial(n) * inverse(factorial(r)) * inverse(factorial(n - r))) % MOD

    def weighted_sum_of_partitions(n, k, w):
        total_sum = 0
        for r in range(1, k + 1):
            total_sum = (total_sum + combination(k, r) * (w[r-1] ** r) * factorial(n - r)) % MOD
        return total_sum

    result = weighted_sum_of_partitions(n, k, w)
    print(result)

solve()

def solve():
    n = int(input())
    x = list(map(int, input().split()))
    edges = []
    for _ in range(n - 1):
        a, b = map(int, input().split())
        edges.append((a - 1, b - 1))

    def gcd(a, b):
        if b == 0:
            return a
        return gcd(b, a % b)

    def dfs(node, parent):
        nonlocal x
        nonlocal edges
        nonlocal result
        nonlocal mod

        result[node] = x[node]
        for neighbor in edges:
            if neighbor[0] == node:
                if neighbor[1] != parent:
                    dfs(neighbor[1], node)
                    result[node] = gcd(result[node], result[neighbor[1]])
            elif neighbor[1] == node:
                if neighbor[0] != parent:
                    dfs(neighbor[0], node)
                    result[node] = gcd(result[node], result[neighbor[0]])

    result = [0] * n
    mod = 10**9 + 7
    dfs(0, -1)
    total_sum = 0
    for i in range(n):
        total_sum = (total_sum + result[i]) % mod
    print(total_sum)

