def solve():
    R, C = map(int, input().split())
    maze = []
    for _ in range(R):
        maze.append(input())

    def is_valid(x, y):
        return 0 <= x < R and 0 <= y < C

    def get_neighbors(x, y):
        neighbors = []
        if is_valid(x-1, y):
            neighbors.append((x-1, y))
        if is_valid(x+1, y):
            neighbors.append((x+1, y))
        if is_valid(x, y-1):
            neighbors.append((x, y-1))
        if is_valid(x, y+1):
            neighbors.append((x, y+1))
        return neighbors

    def get_region(x, y):
        region = set()
        stack = [(x, y)]
        visited = set()
        visited.add((x, y))
        while stack:
            x, y = stack.pop()
            region.add((x, y))
            for nx, ny in get_neighbors(x, y):
                if (nx, ny) not in visited and maze[nx][ny] != '#':
                    stack.append((nx, ny))
                    visited.add((nx, ny))
        return region

    def get_odd_even_regions():
        odd_regions = []
        even_regions = []
        for x in range(R):
            for y in range(C):
                if (x + y) % 2 == 0:
                    region = get_region(x, y)
                    if region:
                        even_regions.append(region)
                else:
                    region = get_region(x, y)
                    if region:
                        odd_regions.append(region)
        return odd_regions, even_regions

    odd_regions, even_regions = get_odd_even_regions()

    def is_connected(region):
        visited = set()
        stack = [region.pop()]
        while stack:
            x, y = stack.pop()
            visited.add((x, y))
            for nx, ny in get_neighbors(x, y):
                if (nx, ny) in region and (nx, ny) not in visited:
                    stack.append((nx, ny))
        return len(visited) == len(region)

    def get_connected_components(regions):
        connected_components = []
        visited = set()
        for region in regions:
            if region:
                if region not in visited:
                    component = set()
                    stack = [region.pop()]
                    while stack:
                        x, y = stack.pop()
                        component.add((x, y))
                        visited.add((x, y))
                        for nx, ny in get_neighbors(x, y):
                            if (nx, ny) in region and (nx, ny) not in visited:
                                stack.append((nx, ny))
                    connected_components.append(component)
        return connected_components

    odd_connected_components = get_connected_components(odd_regions)
    even_connected_components = get_connected_components(even_regions)

    def is_valid_odd_even_split(odd_component, even_component):
        for x, y in odd_component:
            if (x + y) % 2 == 0:
                if (x, y) not in even_component:
                    return False
        for x, y in even_component:
            if (x + y) % 2 != 0:
                if (x, y) not in odd_component:
                    return False
        return True

    def get_valid_odd_even_splits(odd_component, even_component):
        valid_splits = []
        for x, y in odd_component:
            if (x + y) % 2 == 0:
                if (x, y) not in even_component:
                    new_odd_component = odd_component.copy()
                    new_odd_component.remove((x, y))
                    new_even_component = even_component.copy()
                    new_even_component.add((x, y))
                    valid_splits.append((new_odd_component, new_even_component))
        for x, y in even_component:
            if (x + y) % 2 != 0:
                if (x, y) not in odd_component:
                    new_odd_component = odd_component.copy()
                    new_odd_component.add((x, y))
                    new_even_component = even_component.copy()
                    new_even_component.remove((x, y))
                    valid_splits.append((new_odd_component, new_even_component))
        return valid_splits

    def get_min_walls_to_remove(odd_component, even_component):
        if not odd_component and not even_component:
            return 0
        if not odd_component:
            return len(even_component)
        if not even_component:
            return len(odd_component)
        min_walls = float('inf')
        for new_odd_component, new_even_component in get_valid_odd_even_splits(odd_component, even_component):
            if is_valid_odd_even_split(new_odd_component, new_even_component):
                min_walls = min(min_walls, get_min_walls_to_remove(new_odd_component, new_even_component))
        return min_walls

