def solve():
    n = int(input())
    coins = [2**i for i in range(1, n+1)]

    total_weight = sum(coins)
    half_weight = total_weight // 2

    def find_min_difference(coins, half_weight):
        n = len(coins)
        dp = [[float('inf')] * (half_weight + 1) for _ in range(1 << n)]

        for i in range(1 << n):
            for j in range(half_weight + 1):
                if j == 0:
                    dp[i][j] = 0
                    continue

                for k in range(n):
                    if (i & (1 << k)) == 0:
                        continue
                    if j >= coins[k]:
                        dp[i][j] = min(dp[i][j], dp[i ^ (1 << k)][j - coins[k]])
                    else:
                        dp[i][j] = min(dp[i][j], dp[i ^ (1 << k)][j])

        return dp[(1 << n) - 1][half_weight]

    print(find_min_difference(coins, half_weight))

