        if expression.isdigit():
            return int(expression)
        elif expression[0] == '(':
            return evaluate(expression[1:-1])
        else:
            left = evaluate(expression[:expression.find('?')])
            right = evaluate(expression[expression.find('?') + 1:])
            if expression[expression.find('?')] == '+':
                return left + right
            else:
                return left - right

    max_value = float('-inf')
    for i in range(2 ** (plus_count + minus_count)):
        operators = ['?'] * (plus_count + minus_count)
        for j in range(plus_count + minus_count):
            if i & (1 << j):
                operators[j] = '+'
            else:
                operators[j] = '-'

        current_value = evaluate(expression)
        max_value = max(max_value, current_value)

    print(max_value)

