    frogs = []
    for _ in range(n):
        x, d = map(int, input().split())
        frogs.append((x, d))

    max_position = 0
    max_size = 0

    for i in range(n):
        for j in range(i + 1, n):
            x1, d1 = frogs[i]
            x2, d2 = frogs[j]

            if x1 > x2:
                continue

            position = x1
            size = 1
            while True:
                position += d1
                if position > x2:
                    break
                size += 1

                position += d2
                if position > x2:
                    break
                size += 1

            if position > x2:
                continue

            if position > max_position:
                max_position = position
                max_size = size
            elif position == max_position:
                max_size = max(max_size, size)

    print(max_position, max_size)

