def solve():
    n, k = map(int, input().split())
    s = input()

    def transform(s):
        new_s = ""
        for i in range(n):
            if s[i] == s[(i + 1) % n]:
                new_s += "B"
            else:
                new_s += "W"
        return new_s

    def rotate(s):
        for _ in range(n):
            s = s[1:] + s[0]
            yield s

    def count_distinct(startings):
        seen = set()
        for s in starting:
            seen.add(s)
            for _ in range(k):
                s = transform(s)
                seen.add(s)
        return len(seen)

    starting = []
    for s in rotate(s):
        starting.append(s)

    print(count_distinct(starting))

