        cities[city] = i

    graph = {}
    for _ in range(m):
        city1, city2, cost = input().split()
        cost = int(cost)
        if city1 not in graph:
            graph[city1] = {}
        if city2 not in graph:
            graph[city2] = {}
        graph[city1][city2] = cost
        graph[city2][city1] = cost

    assignments = []
    for _ in range(4):
        city1, city2 = input().split()
        assignments.append((city1, city2))

    def dijkstra(start):
        distances = {city: float('inf') for city in cities}
        distances[start] = 0
        queue = [(0, start)]
        while queue:
            current_distance, current_city = heapq.heappop(queue)
            if current_distance > distances[current_city]:
                continue
            for neighbor, weight in graph[current_city].items():
                distance = current_distance + weight
                if distance < distances[neighbor]:
                    distances[neighbor] = distance
                    heapq.heappush(queue, (distance, neighbor))
        return distances

    min_cost = float('inf')
    for assignment in itertools.permutations(assignments):
        cost = 0
        for city1, city2 in assignment:
            distances = dijkstra(city1)
            cost += distances[city2]
        min_cost = min(min_cost, cost)

    print(min_cost)

