def solve():
    n, x, y = map(int, input().split())

    mod = 10**9 + 7

    def binomial_coefficient(n, k):
        if k > n:
            return 0
        if k == 0 or k == n:
            return 1

        k = min(k, n - k)

        result = 1
        for i in range(k):
            result = (result * (n - i)) % mod
            result = (result * pow(i + 1, mod - 2, mod)) % mod

        return result

    def count_paths(n, x, y):
        if x == 1 and y == 1:
            return 1

        if x == 1:
            return count_paths(n, 1, y - 1)

        if y == 1:
            return count_paths(n, x - 1, 1)

        return (count_paths(n - x, 1, y) + count_paths(n - y, x, 1)) % mod

    print(count_paths(n, x, y))

solve()

