        result = 1
        for i in range(1, n + 1):
            result = (result * i) % MOD
        return result

    def choose(n, k):
        if k > n:
            return 0
        return (factorial(n) * pow(factorial(k), MOD - 2, MOD) * pow(factorial(n - k), MOD - 2, MOD)) % MOD

