            parent[x] = find(parent[x])
        return parent[x]

    def union(x, y):
        root_x = find(x)
        root_y = find(y)
        if root_x != root_y:
            parent[root_x] = root_y

    parent = list(range(n + 1))
    for a, b in edges:
        union(a, b)

    def dfs(node, parent_node, x, y, dx, dy):
        if node == 1:
            return x, y
        parent[node] = parent_node
        nx = x + dx
        ny = y + dy
        for a, b in edges:
            if a == node:
                nx = x + dx
                ny = y + dy
                break
            elif b == node:
                nx = x - dx
                ny = y - dy
                break
        return dfs(a, node, nx, ny, dx, dy), dfs(b, node, nx, ny, dx, dy)

