def solve():
    import math

    def get_touches(image):
        touches = []
        for i in range(15):
            for j in range(30):
                if image[i][j] == 'X':
                    touches.append((i, j))
        return touches

    def get_grip(image):
        touches = get_touches(image)
        if not touches:
            return None, None

        grip_points = []
        for i in range(15):
            for j in range(30):
                if image[i][j] == 'X':
                    grip_points.append((i, j))
        if not grip_points:
            return None, None

        grip_x = sum(x for x, y in grip_points) / len(grip_points)
        grip_y = sum(y for x, y in grip_points) / len(grip_points)
        grip_point = (grip_x, grip_y)

        return grip_point, touches

    def get_touch_vector(grip_point, touch_point):
        return (touch_point[0] - grip_point[0], touch_point[1] - grip_point[1])

    def get_distance(p1, p2):
        return math.sqrt((p1[0] - p2[0])**2 + (p1[1] - p2[1])**2)

    def get_angle(v1, v2):
        dot_product = v1[0] * v2[0] + v1[1] * v2[1]
        magnitude_v1 = math.sqrt(v1[0]**2 + v1[1]**2)
        magnitude_v2 = math.sqrt(v2[0]**2 + v2[1]**2)
        cos_theta = dot_product / (magnitude_v1 * magnitude_v2)
        angle = math.acos(cos_theta)
        if v1[0] * v2[1] - v1[1] * v2[0] < 0:
            angle = -angle
        return angle

    def get_grip_spread(grip_point, touches):
        spread = 0
        for touch_point in touches:
            spread += get_distance(grip_point, touch_point)
        return spread / len(touches)

    def get_grip_rotation(grip_point, touches):
        total_rotation = 0
        for i in range(len(touches)):
            touch_vector = get_touch_vector(grip_point, touches[i])
            next_touch_vector = get_touch_vector(grip_point, touches[(i + 1) % len(touches)])
            total_rotation += get_angle(touch_vector, next_touch_vector)
        return total_rotation / len(touches)

    def get_rotation_distance(grip_spread, grip_rotation):
        return abs(grip_rotation) * grip_spread

    def classify_gesture(initial_grip, final_grip):
        initial_grip_point, initial_touches = initial_grip
        final_grip_point, final_touches = final_grip

        initial_grip_spread = get_grip_spread(initial_grip_point, initial_touches)
        final_grip_spread = get_grip_spread(final_grip_point, final_touches)

        initial_grip_rotation = get_grip_rotation(initial_grip_point, initial_touches)
        final_grip_rotation = get_grip_rotation(final_grip_point, final_touches)

        pan_distance = get_distance(initial_grip_point, final_grip_point)
        zoom_distance = abs(final_grip_spread - initial_grip_spread)
        rotation_distance = get_rotation_distance(initial_grip_spread, initial_grip_rotation)

        if pan_distance > zoom_distance and pan_distance > rotation_distance:
            print(f"{len(initial_touches)} pan")
        elif zoom_distance > pan_distance and zoom_distance > rotation_distance:
            if final_grip_spread < initial_grip_spread:
                print(f"{len(initial_touches)} zoom out")
            else:
                print(f"{len(initial_touches)} zoom in")
        else:
            print(f"{len(initial_touches)} rotate {math.degrees(initial_grip_rotation)}")

    image1 = []
    image2 = []
    for _ in range(15):
        line1 = input()
        line2 = input()
        image1.append(line1)
        image2.append(line2)

    initial_grip = get_grip(image1)
    final_grip = get_grip(image2)

    classify_gesture(initial_grip, final_grip)

solve()

