        edges_a.append((u - 1, v - 1))

    m = int(input())
    edges_b = []
    for _ in range(m - 1):
        u, v = map(int, input().split())
        edges_b.append((u - 1, v - 1))

    def find(parent, x):
        if parent[x] != x:
            parent[x] = find(parent, parent[x])
        return parent[x]

    def union(parent, x, y):
        root_x = find(parent, x)
        root_y = find(parent, y)
        if root_x != root_y:
            parent[root_x] = root_y

    def calculate_cost(edges):
        parent = list(range(len(edges)))
        for u, v in edges:
            union(parent, u, v)
        cost = 0
        for i in range(len(edges)):
            root_i = find(parent, i)
            for j in range(i + 1, len(edges)):
                root_j = find(parent, j)
                if root_i == root_j:
                    cost += 1
        return cost * cost

    min_cost = float('inf')
    for u in range(n):
        for v in range(m):
            new_edges_a = edges_a[:]
            new_edges_b = edges_b[:]
            new_edges_a.append((u, v))
            new_edges_b.append((v, u))
            cost_a = calculate_cost(new_edges_a)
            cost_b = calculate_cost(new_edges_b)
            min_cost = min(min_cost, cost_a + cost_b)

    print(min_cost)

solve()

