def solve():
    n = int(input())
    edges = []
    for _ in range(2 * (n - 1)):
        u, v = map(int, input().split())
        edges.append((u, v))

    def is_valid_tree(edges):
        if len(edges) != n - 1:
            return False

        visited = [False] * n
        visited[0] = True
        stack = [0]

        while stack:
            node = stack.pop()
            for u, v in edges:
                if u == node:
                    if not visited[v - 1]:
                        visited[v - 1] = True
                        stack.append(v - 1)
                elif v == node:
                    if not visited[u - 1]:
                        visited[u - 1] = True
                        stack.append(u - 1)

        return all(visited)

    def find_left_edges(edges):
        left_edges = []
        for u, v in edges:
            if u == 1:
                left_edges.append((u, v))
            elif v == n:
                left_edges.append((u, v))
        return left_edges

    def find_right_edges(edges):
        right_edges = []
        for u, v in edges:
            if u == n:
                right_edges.append((u, v))
            elif v == 1:
                right_edges.append((u, v))
        return right_edges

    left_edges = find_left_edges(edges)
    right_edges = find_right_edges(edges)

    if not is_valid_tree(left_edges) or not is_valid_tree(right_edges):
        print("impossible")
        return

    left_edges.sort(key=lambda x: x[1])
    right_edges.sort(key=lambda x: x[1])

    result = ""
    for u, v in left_edges:
        result += "L"
    for u, v in right_edges:
        result += "R"

    print(result)

solve()

