def solve():
    s = input()
    n = len(s)

    def weight(factoring):
        weight = 0
        i = 0
        while i < len(factoring):
            if factoring[i] == '(':
                j = i + 1
                while factoring[j] != ')':
                    j += 1
                exponent = ''
                k = j + 1
                while k < len(factoring) and factoring[k].isdigit():
                    exponent += factoring[k]
                    k += 1
                weight += (j - i - 1) * int(exponent)
                i = k
            else:
                weight += 1
                i += 1
        return weight

    def find_maximal_factoring(s):
        n = len(s)
        max_weight = 0
        best_factoring = ""

        for i in range(1, n):
            for j in range(i + 1, n + 1):
                substring = s[i:j]
                if s[:i] + substring + s[j:] == s:
                    factoring = "(" + substring + ")" + str((j - i) // len(substring))
                    current_weight = weight(factoring)
                    if current_weight > max_weight:
                        max_weight = current_weight
                        best_factoring = factoring

        return best_factoring

    maximal_factoring = find_maximal_factoring(s)
    print(weight(maximal_factoring))

solve()

