        visited = [False] * (n + 1)
        def dfs(node):
            visited[node] = True
            for u, v in edges:
                if u == node:
                    if not visited[v]:
                        if dfs(v):
                            return True
                    else:
                        return True
                elif v == node:
                    if not visited[u]:
                        if dfs(u):
                            return True
                    else:
                        return True
            return False

        for i in range(1, n + 1):
            if not visited[i]:
                if dfs(i):
                    return False
        return True

    if is_valid_orientation():
        print("YES")
