    n = len(s)
    nums = []
    ops = []
    i = 0
    while i < n:
        if s[i].isdigit():
            j = i
            while j < n and s[j].isdigit():
                j += 1
            nums.append(int(s[i:j]))
            i = j
        else:
            ops.append(s[i])
            i += 1

    def evaluate(nums, ops):
        if len(nums) == 1:
            return [nums[0]]

        result = []
        for i in range(len(ops)):
            left = evaluate(nums[:i+1], ops[:i])
            right = evaluate(nums[i+1:], ops[i+1:])

            for l in left:
                for r in right:
                    if ops[i] == '+':
                        result.append(l + r)
                    else:
                        result.append(str(l) + str(r))

        return result

    distinct_values = evaluate(nums, ops)
    print(len(set(distinct_values)))

solve()

