def solve():
    n, m = map(int, input().split())
    segments = []
    for _ in range(n):
        l, r = map(int, input().split())
        segments.append((l, r))

    segments.sort()

    result = []
    current_right = 0
    for l, r in segments:
        if l > current_right:
            result.extend(range(current_right + 1, l))
        current_right = max(current_right, r)

    if current_right < m:
        result.extend(range(current_right + 1, m + 1))

    if not result:
        print(0)
    else:
        print(len(result))
        print(*result)

solve()

