def is_good(n):
    powers = []
    i = 0
    while 3**i <= n:
        powers.append(3**i)
        i += 1
    for i in range(1 << len(powers)):
        sum_powers = 0
        for j in range(len(powers)):
            if (i >> j) & 1:
                sum_powers += powers[j]
        if sum_powers == n:
            return True
    return False

