def solve():
    n, k = map(int, input().split())
    a = list(map(int, input().split()))

    left = 0
    right = n - 1
    solved = 0

    while left <= right:
        if left == right:
            if a[left] <= k:
                solved += 1
            break

        if a[left] <= k:
            solved += 1
            left += 1
        elif a[right] <= k:
            solved += 1
            right -= 1
        else:
            break

    print(solved)

solve()

def solve():
    s = input()
    p = input()

    if s == p:
        print("Yes")
        return

    if len(s) == len(p) + 1:
        if s[:-1] == p:
            print("Yes")
            return

    if len(s) == len(p) - 1:
        if s[1:] == p:
            print("Yes")
            return

    reversed_p = ""
    for char in p:
        if 'a' <= char <= 'z':
            reversed_p += char.upper()
        elif 'A' <= char <= 'Z':
            reversed_p += char.lower()
        else:
            reversed_p += char

    if reversed_p == s:
        print("Yes")
        return

    print("No")

